using UTSMedicalSystem.FrontEnd.Controllers; using Microsoft.AspNetCore.Mvc;  using Microsoft.EntityFrameworkCore; using Microsoft.VisualStudio.TestTools.UnitTesting; using UTSMedicalSystem.FrontEnd.Data; using System.Threading.Tasks;

namespace UnitTestProject.Testing {     [TestClass]     public class ControllerTesting     {         //Testing HomeController         [TestMethod]         public void VerifyHCIdexViewType()         {             //Arrange             var controller = new HomeController();              //Act             var result = controller.Index();              //Assert             Assert.IsNotNull(result);             Assert.IsInstanceOfType(result,typeof(ViewResult));          }           [TestMethod]         public void VerifyHCAboutViewType()         {             //Arrange             var controller = new HomeController();              //Act             var result = controller.About();              //Assert             Assert.IsNotNull(result);             Assert.IsInstanceOfType(result, typeof(ViewResult));          }           [TestMethod]         public void VerifyHCContactViewType()         {             //Arrange             var controller = new HomeController();              //Act             var result = controller.Contact();              //Assert             Assert.IsNotNull(result);             Assert.IsInstanceOfType(result, typeof(ViewResult));          }         //Testing PatientController          [TestMethod]         public void VerifyPCIdexViewType()         {             //Arrange             var options = new DbContextOptionsBuilder<MedicalSystemContext>()                 .UseInMemoryDatabase(databaseName: "Testing")                 .Options;             var context = new MedicalSystemContext(options) ;              var controller = new PatientsController(context);              var result = controller.Index();              //Assert             Assert.IsNotNull(result);             Assert.IsInstanceOfType(result, typeof(Task<IActionResult>));         }     } } 